import React from "react";
import ReactDOM from "react-dom";
import Greece from "@svg-maps/greece";
import { SVGMap } from "react-svg-map";
import { Container, Row, Col, Navbar, Nav, Form, FormControl, Button} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './svg-map.css';

export default class App extends React.Component {
    constructor(props) {
        super(props);
        //state objects
        this.state = {
            city: null,
            city_coin: null,
            city_desc: null,
            region: null,
            pic: null,
            test: null
        }
        //functions
        this.locMouseOver = this.locMouseOver.bind(this)
    }

    async locMouseOver(input) {

        const reg_name = input.target.attributes.name.value


        
        //const url = "http://127.0.0.1:4000/find?region=" + reg_name
        //fetch(url).then(res => {
        //    if (res.ok) {
        //        return res.json()
        //    } else {
        //        throw new Error("Something went wrong")
        //    }
        //}).then(data => {
        //    console.log(data[0].coin_img)
        //    this.setState({
        //        city: data[0].city_name,
        //        city_coin: data[0].city_coin,
        //        city_desc: data[0].city_desc,
        //        region: data[0].coin_region,
        //        pic: "http://127.0.0.1:4000/imgfeed/"+data[0].coin_img
        //    })
        //}).catch (err => {
        //    console.log(err)
        //})

    }

    render() {
        //logic based jsx elements
        let img_state = null

        if (this.state.pic != null) {
            img_state = <img src={this.state.pic}></img>
        } else {
            img_state = <p>None</p>
        }
        //normie jsx elements section

        //navbar
        var navbar = (
            <Navbar bg="dark" variant="dark" >
                <Navbar.Brand href="#home">
                    ANCIENT COINS OF GREECE
                    </Navbar.Brand>
                <Nav className="mr-auto">
                    <Nav.Link href = "#about">About</Nav.Link>
                </Nav>
                <Form inline onSubmit={this.locMouseOver}>
                    <FormControl type="text" placeholder="search for coin" className="mr-sm-2" />
                    <Button variant="outline-light" type="submit">Search</Button>
                </Form>
            </Navbar>
        )


        return (
            //main boi
            <Container fluid>
                {navbar}
                <Row>
                    <Col sm={8}>
                        <SVGMap
                            map={Greece}
                            onLocationMouseOver={this.locMouseOver}
                         />
                    </Col>

                    <Col sm={4}>
                        {this.state.city}
                        {this.state.city_coin}
                        {this.state.city_desc}
                        {this.state.region}
                        {img_state}
                    </Col>
                </Row>
            </Container>
        );
    }
}
