import React, {useState} from "react";
import ReactDOM from "react-dom";
import Greece from "@svg-maps/greece";
import { RadioSVGMap  } from "react-svg-map";
import { Container, Row, Col, Card} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import Footer from './components/footer.js';
import NVBar from './components/navbar.js';
import GreeceSVG from './greece.js';
function App(){
    let coin_display;

    const [coin, getCoin] = useState({
            city: null,
            city_coin: null,
            city_desc: null,
            region: null,
            pic: null,
            trigger: false
        })

    const fetchAPI = async (value) =>{
        const url = "http://127.0.0.1:4000/find?region=" + value
        fetch(url).then(res => {
            if (res.ok) {
                return res.json()
            } else {
                throw new Error("Something went wrong")
            }
        }).then(data => {
            getCoin({
                city: data[0].city_name,
                city_coin: data[0].city_coin,
                city_desc: data[0].city_desc,
                region: data[0].coin_region,
                pic: "http://127.0.0.1:4000/imgfeed/" + data[0].coin_img
            })
        }).catch(err => {
            console.log(err)
        })
    }
    
    //unused function
    const locMouseOver = (input) =>{
        const reg_name = input.target.attributes.name.value
        fetchAPI(reg_name)
    }

    const closePic = () => {
        getCoin({
            city: null,
            city_coin: null,
            city_desc: null,
            region: null,
            pic: null,
            trigger: false
        })
    }

    if(coin.pic!= null) {
        coin_display = (
            <Card >
                <button id="b-right" onClick={closePic}>X</button>
                <Card.Img variant="top" src={coin.pic}></Card.Img>
                <Card.Title>{coin.city_coin}</Card.Title>
                <Card.Text>
                    <p>City: {coin.city}</p>
                    <p>Region: {coin.region}</p>
                    <p>Description: {coin.city_desc}</p>
                </Card.Text>
            </Card>
        )
    } else {
        coin_display = null
    }
    //normie jsx elements section

    return (
        //main boi
        <Container>
            <NVBar/>
            <Row>
                <Col sm={9}>
                    <GreeceSVG getData = {fetchAPI} trigger = {coin.trigger} />
                </Col>

                <Col sm={3}>
                    {coin_display}
                </Col>
            </Row>
            <Footer/>
        </Container>
    )
}

export default App;
