import React from "react";
import ReactDOM from "react-dom";
import Greece from "@svg-maps/greece";
import { RadioSVGMap  } from "react-svg-map";
import { Container, Row, Col, Card} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import Footer from './components/footer.js';
import NVBar from './components/navbar.js';
import GreeceSVG from './greece.js';
export default class App extends React.Component {
    constructor(props) {
        super(props);
        //state objects
        this.state = {
            city: null,
            city_coin: null,
            city_desc: null,
            region: null,
            pic: null,
            trigger: false
        }
        //functions
        this.locMouseOver = this.locMouseOver.bind(this)
        this.closePic = this.closePic.bind(this)
        this.fetchAPI = this.fetchAPI.bind(this)
    }

    async fetchAPI(value) {
        const url = "http://127.0.0.1:4000/find?region=" + value
        fetch(url).then(res => {
            if (res.ok) {
                return res.json()
            } else {
                throw new Error("Something went wrong")
            }
        }).then(data => {
            this.setState({
                city: data[0].city_name,
                city_coin: data[0].city_coin,
                city_desc: data[0].city_desc,
                region: data[0].coin_region,
                pic: "http://127.0.0.1:4000/imgfeed/" + data[0].coin_img
            })
        }).catch(err => {
            console.log(err)
        })
    }

    locMouseOver(input) {
        const reg_name = input.target.attributes.name.value
        this.fetchAPI(reg_name)
    }

    closePic() {
        this.setState({pic: null})
    }

    render() {
        //logic based jsx elements
        var coin_display = null

        if (this.state.pic != null) {
            coin_display = (
                <Card >
                    <button id="b-right" onClick={this.closePic}>X</button>
                    <Card.Img variant="top" src={this.state.pic}></Card.Img>
                    <Card.Title>{this.state.city_coin}</Card.Title>
                    <Card.Text>
                        <p>City: {this.state.city}</p>
                        <p>Region: {this.state.region}</p>
                        <p>Description: {this.state.city_desc}</p>
                    </Card.Text>
                </Card>
            )
        } else {
            coin_display = null
        }
        //normie jsx elements section

        return (
            //main boi
            <Container>
                <NVBar/>
                <Row>
                    <Col sm={8}>
                        <GreeceSVG getData = {this.fetchAPI} trigger = {this.state.trigger} style={{ height: 792, width: 918 }}/>
                    </Col>

                    <Col sm={4}>
                        {coin_display}
                    </Col>
                </Row>
                <Footer/>
            </Container>
        );
    }
}
