import React from "react";
import ReactDOM from "react-dom";
import Greece from "@svg-maps/greece";
import { SVGMap } from "react-svg-map";
import { Container, Row, Col, Navbar, Nav, Form, FormControl, Button} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './svg-map.css';

export default class App extends React.Component {
    constructor(props) {
        super(props);
        //state objects
        this.state = {
            city: null,
            city_coin: null,
            city_desc: null,
            region: null,
            pic: null,
            search_key: '',
            warning: 'enter a region to search for coin'
        }
        //functions
        this.locMouseOver = this.locMouseOver.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.fetchAPI = this.fetchAPI.bind(this)
    }

    async fetchAPI(value) {
        const url = "http://127.0.0.1:4000/find?region=" + value
        fetch(url).then(res => {
            if (res.ok) {
                return res.json()
            } else {
                throw new Error("Something went wrong")
            }
        }).then(data => {
            console.log(data[0].coin_img)
            this.setState({
                city: data[0].city_name,
                city_coin: data[0].city_coin,
                city_desc: data[0].city_desc,
                region: data[0].coin_region,
                pic: "http://127.0.0.1:4000/imgfeed/" + data[0].coin_img
            })
        }).catch(err => {
            console.log(err)
        })
    }

    locMouseOver(input) {
        const reg_name = input.target.attributes.name.value
        this.fetchAPI(reg_name)
    }

    handleSubmit() {

        if (this.state.search == null) {
            this.setState({ warning: "Please enter a coin to search." })
        } else {
            let input = this.state.search_key
            this.setState({ warning: null })
            this.fetchAPI(input)
        }
    }

    render() {
        //logic based jsx elements
        let img_state = null

        if (this.state.pic != null) {
            img_state = <img src={this.state.pic}></img>
        } else {
            img_state = <p>None</p>
        }
        //normie jsx elements section

        //navbar
        var navbar = (
            <Navbar bg="dark" variant="dark" >
                <Navbar.Brand href="#home">
                    ANCIENT COINS OF GREECE
                </Navbar.Brand>
                <Nav className="mr-auto">
                    <Nav.Link href = "#about">About</Nav.Link>
                </Nav>
                <Form inline>
                    <FormControl type="text" placeholder={this.state.warning} className="mr-sm-2" value={this.state.search_key} onChange={(event) => this.setState({ search_key: event.target.value })} />
                    <Button variant="outline-light" type="button" onClick={this.handleSubmit}>Search</Button>
                </Form>
            </Navbar>
        )


        return (
            //main boi
            <Container fluid>
                {navbar}
                <Row>
                    <Col sm={8}>
                        <SVGMap
                            map={Greece}
                            onLocationMouseOver={this.locMouseOver}
                         />
                    </Col>

                    <Col sm={4}>
                        {this.state.city}
                        {this.state.city_coin}
                        {this.state.city_desc}
                        {this.state.region}
                        {img_state}
                    </Col>
                </Row>
            </Container>
        );
    }
}
