import React from "react";
import ReactDOM from "react-dom";
import Greece from "@svg-maps/greece";
import { SVGMap  } from "react-svg-map";
import { Container, Row, Col, Navbar, Nav, Form, FormControl, Button, Card} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './svg-map.css';
import Footer from './components/footer.js';

export default class App extends React.Component {
    constructor(props) {
        super(props);
        //state objects
        this.state = {
            city: null,
            city_coin: null,
            city_desc: null,
            region: null,
            pic: null,
            search_key: '',
            warning: '',
            mr_satan: ''
        }
        //functions
        this.locMouseOver = this.locMouseOver.bind(this)
        this.locMouseOff = this.locMouseOff.bind(this)
        this.locMouseClick = this.locMouseClick(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.fetchAPI = this.fetchAPI.bind(this)
    }

    async fetchAPI(value) {
        const url = "http://127.0.0.1:4000/find?region=" + value
        fetch(url).then(res => {
            if (res.ok) {
                return res.json()
            } else {
                throw new Error("Something went wrong")
            }
        }).then(data => {
            this.setState({
                city: data[0].city_name,
                city_coin: data[0].city_coin,
                city_desc: data[0].city_desc,
                region: data[0].coin_region,
                pic: "http://127.0.0.1:4000/imgfeed/" + data[0].coin_img
            })
        }).catch(err => {
            console.log(err)
        })
    }

    locMouseOver(input) {
        const reg_name = input.target.attributes.name.value
        this.fetchAPI(reg_name)
    }

    locMouseOff() {
        this.setState({pic: null})
    }

    locMouseClick(lemon) {
        this.setState({mr_satan: "SUPERRRR"})
    }

    handleSubmit() {
        if (this.state.search_key == '') {
            this.setState({ warning: "Please enter a coin to search." })
        } else {
            let input = this.state.search_key
            this.setState({ warning: '' })
            this.fetchAPI(input)
        }
    }

    render() {
        //logic based jsx elements
        var coin_display = null

        if (this.state.pic != null) {
            coin_display = (
                <Card >
                    <Card.Img variant="top" src={this.state.pic}></Card.Img>
                    <Card.Title>{this.state.city_coin}</Card.Title>
                    <Card.Text>
                        <p>City: {this.state.city}</p>
                        <p>Region: {this.state.region}</p>
                        <p>Description: {this.state.city_desc}</p>
                    </Card.Text>
                </Card>
            )
        } else {
            coin_display = null
        }
        //normie jsx elements section
        //navbar
        var navbar = (
            <Navbar bg="dark" variant="dark">
                <Navbar.Brand href="#home">
                    ANCIENT COINS OF GREECE
                </Navbar.Brand>
                <Nav className="mr-auto">
                    <Nav.Link href = "#about">About</Nav.Link>
                </Nav>
                {this.state.warning}
                <Form inline>
                    <FormControl type="text" placeholder="search for coin" className="mr-sm-2" value={this.state.search_key} onChange={(event) => this.setState({ search_key: event.target.value })} />
                    <Button variant="outline-light" type="button" onClick={this.handleSubmit}>Search</Button>
                </Form>
            </Navbar>
        )

        
        return (
            //main boi
            <Container>
                {this.state.mr_satan}
                {navbar}
                <Row>
                    <Col sm={8}>
                        <SVGMap 
                            map={Greece}
                            onLocationFocus={this.locMouseClick}
                            onLocationMouseOver={this.locMouseOver}
                            onLocationMouseOut={this.locMouseOff}
                         />
                    </Col>

                    <Col sm={4}>
                        {coin_display}
                    </Col>
                </Row>
                <Footer/>
            </Container>
        );
    }
}
